function scrolltotop(){0!=window.scrollY&&setTimeout(()=>{window.scrollTo(0,window.scrollY-30),scrolltotop()},5)}function cookies(){"true"==localStorage.getItem("cookies")&&(document.querySelector(".cookies").style.display="none")}function setCookies(){localStorage.setItem("cookies","true"),cookies()}function theme(){"light"==localStorage.getItem("theme")&&(style.href="/assets/css/light.css")}function setTheme(){null==localStorage.getItem("theme")||"dark"==localStorage.getItem("theme")?(localStorage.setItem("theme","light"),style.href="/assets/css/light.css"):"light"==localStorage.getItem("theme")&&(localStorage.setItem("theme","dark"),style.href="/assets/css/dark.css")}window.onscroll=(()=>{let e=(document.body.scrollTop||document.documentElement.scrollTop)/(document.documentElement.scrollHeight-document.documentElement.clientHeight)*100;document.querySelector("#progress-bar").style.width=e+"%"}),document.querySelector("#gotop").addEventListener("click",e=>{e.preventDefault(),scrolltotop()}),document.querySelector("#btnacceptcookies").addEventListener("click",e=>{e.preventDefault(),setCookies()}),document.querySelector("#theme").addEventListener("click",e=>{e.preventDefault(),setTheme()});